const Discord = require("discord.js");
const fs = require('fs');
var embed = [];
var mainMessage;

//Setings
var timezone = "UTC"; // Default: UTC || Takes abbreviation timezones like EST, EDT, PST, etc.
var debug = false; // Default: false || Prints any errors that may have occured; will also print for await messages that ran out of time

try {
 new Date().toLocaleString("en-US", {
 timeZone: timezone
 })
} catch (e) {
 console.log(`${timezone} is an invalid timezone!\nResetting back to UTC.`);
 timezone = "UTC";
}

// Above checks validity of timezone, will reset to UTC if invalid.
// Please do not modify!

let embedTemplate = {
 color: 0xFFFF00,
 author: {
 name: client.user.username,
 icon_url: client.user.avatarURL
 },
    title: "Configuração do Sorteio",
 description: "This is a test embed to showcase what they look like and what they can do.",
 timestamp: new Date(),
}

var giveaway = {
 "itemGivingAway": "",
 "time": 0,
 "winner": 1,
    "finishUnix": 0,
 "channel": "",
 "server": msg.guild.id,
 "giveawayMessageID": "",
 "id": Math.floor(Math.random() * 999999) + 100000
}


if (msg.member.hasPermission('MANAGE_CHANNELS')) {
 embedTemplate["description"] = `Bem-vindo ao prompt do sorteio.\n\nEspecifique primeiro o título que você gostaria para o sorteio.`;
 msg.channel.send({embed: embedTemplate})
 .then((message) => {
 mainMessage = message;
 askQuestion(`Bem-vindo ao prompt do sorteio.\n\nEspecifique primeiro o título que você gostaria para o sorteio.`).then((response) => {
 embed[0] = response.content;
 askQuestion(`O título será **${embed[0]}**\n\nSe estiver correto, insira uma descrição!\nSe estiver incorreto, cancele o prompt com "cancelar".`).then((response) => {
 embed[1] = response.content
 if (embed[1].toLowerCase() == "cancelar") {
 errorMessage('Prompt cancelado devido ao cancelamento do usuário. \nDigite novamente o comando para executar o prompt novamente!', "O usuário respondeu com cancelar, cancelando assim o prompt");
 } else {
 embedTemplate["description"] = `O título será **${embed[0]}** e a descrição é:\n\n${embed[1]}\n\nSe estiver correto, responda com "confirmar" para enviar a incorporação!\nCaso contrário, responda com "cancelar"`;
 askQuestion(`O título será **${embed[0]}** e a descrição é:\n\n${embed[1]}\n\nSe estiver correto, responda com "confirmar" para enviar a incorporação!\nCaso contrário, responda com "cancelar"`).then((response) => {
 if (response.content.toLowerCase() == "confirmar") {
 askQuestion(`Agora passando para a configuração do sorteio.\nAgora especifique o que você está sorteando!`).then((response) => {
 giveaway["itemGivingAway"] = response.content;
 askQuestion(`Você está sorteando "${response.content}" correto?\n\nSe sim, responda agora com o número de vencedores que gostaria de ter.\nSe não, responda com qualquer outra coisa.`).then((response) => {
 if (!isNaN(response.content)) {
 if (parseInt(response.content) > 0) {
 giveaway["winner"] = parseInt(response.content);
 askQuestion(`Você está sorteando "${giveaway["itemGivingAway"]}" para ${response.content} user(s).\n\nSe estiver correto, responda agora com a quantidade de tempo para o sorteio.\nEx. 1s | 1d | 1h | 1m`).then((response) => {
 let tempTime = response.content.toLowerCase();
 if (tempTime.includes("d") || tempTime.includes("h") || tempTime.includes("s") || tempTime.includes("m")) {
 let timeUp;
 let timeText;

 if (tempTime.includes("d")) {
 timeUp = "d";
 timeText = "dia(s)";
 } else if (tempTime.includes("h")) {
 timeUp = "h";
 timeText = "hora(s)";
 } else if (tempTime.includes("s")) {
 timeUp = "s";
 timeText = "semana(s)";
 } else if (tempTime.includes("m")) {
 timeUp = "m";
 timeText = "minuto(s)";
 }

 tempTime = tempTime.split(timeUp);
 if (!isNaN(tempTime[0])) {
 tempTime[0] = parseInt(tempTime[0]);
 switch (timeUp) {
 case "d":
 giveaway["time"] = tempTime[0] * 86400;
 break;
 case "h":
 giveaway["time"] = tempTime[0] * 3600;
 break;
 case "s":
 giveaway["time"] = tempTime[0] * 604800;
 break;
 case "m":
 giveaway["time"] = tempTime[0] * 60;
 break;
 }
 askQuestion(`Você está sorteando "${giveaway["itemGivingAway"]}" para ${giveaway["winner"]} user(s)\nO vencedor será sorteado em ${tempTime[0]} ${timeText}\n\nSe estiver correto, mencione agora o canal em que o sorteio será postado.`).then ((response) => {
 if (response.mentions.channels.first() != null) {
 let giveawayChannel = response.mentions.channels.first();
 giveaway["channel"] = giveawayChannel.id;
 giveaway["finishUnix"] = Math.floor(Date.now() / 1000) + giveaway["time"];

 var giveawayEmbed = {
 color: 0x2E7F98,
 author: {
 name: embed[0],
 icon_url: (client || this.getDBM().Bot.bot).user.avatarURL
 },
 description: embed[1],
 fields: [{
 name: '\u200b',
 value: '\u200b',
 inline: false,
 },
 {
 name: 'Item Sorteado',
 value: giveaway["itemGivingAway"],
 inline: true,
 },
 {
 name: 'Sorteio Criado por',
 value: `${msg.author}`,
 inline: true,
 },
 {
 name: 'Sorteio iniciado',
 value: new Date().toLocaleString("en-US", {
 timeZone: timezone
 }),
 inline: true,
 },
 {
 name: 'Sorteio encerrará',
 value: new Date(giveaway["finishUnix"] * 1000).toLocaleString("en-US", {
 timeZone: timezone
 }),
 inline: true,
 },
 {
 name: 'Sorteio Status',
 value: "Ativo/Em andamento",
 inline:true,
 },
 ],
 footer: {
 text: `O sorteio termina em ${new Date(giveaway["finishUnix"] * 1000).toLocaleString("en-US", { timeZone: timezone })} ${timezone.toUpperCase()} | ${giveaway["winner"]} Vencedor(es) | ID: ${giveaway["id"]}`
 }
 }

 giveawayChannel.send({embed: giveawayEmbed})
 .then(giveMsg => {
 giveMsg.react("?");
 giveaway["giveawayMessageID"] = giveMsg.id;
 fs.readFile('./data/giveawayData.json', function read(err, data) {
 if (err) {
 throw err;
 }
 data = JSON.parse(data);

 if (!data.hasOwnProperty(msg.guild.id)) {
 data[msg.guild.id] = [];
 }

 data[msg.guild.id].push(giveaway);

 fs.writeFile("./data/giveawayData.json", JSON.stringify(data), function(err) {
 if (err) {
 return console.log(err);
 }
 });
 });
 });
 mainMessage.delete();
 } else {
 errorMessage("Você não informou um canal válido!\nEx. #exemplo\n\nSaindo da configuração; execute novamente o comando para reiniciar o prompt.", "O usuário não mencionou um canal válido.");
 }
 });
 } else {
 errorMessage("Você não inseriu um formato de hora válido!\nEx. 1s | 1d | 1h | 1m\n\nSaindo da configuração; execute novamente o comando para reiniciar o prompt.", "O usuário não inseriu um formato de hora válido como 1s, 1d, 1m, 1h");
 }
 } else {
 errorMessage("Você não inseriu um formato de hora válido!\nEx. 1s | 1d | 1h | 1m\n\nSaindo da configuração; execute novamente o comando para reiniciar o prompt.", "O usuário não inseriu um formato de hora válido como 1s, 1d, 1m, 1h");
 }
 });

 } else {
 errorMessage("Você não inseriu um número válido de vencedores.\nSaindo da configuração; execute novamente o comando para reiniciar o prompt.", "O usuário não inseriu um número válido de vencedores (número não positivo).");
 }
 } else {
 errorMessage("Você não inseriu um número válido de vencedores.\nSaindo da configuração; execute novamente o comando para reiniciar o prompt.", "O usuário não inseriu um número válido de vencedores");
 }
 });
 });
 } else {
 errorMessage('Prompt cancelado devido à resposta do usuário (o usuário não respondeu com "confirmar").\Digite novamente o comando para executar o prompt novamente!', "O usuário não respondeu com a confirmação e, portanto, o prompt foi cancelado.");
 }
 });
 }
 });
 });
 })
 } else {
 errorMessage("Você não tem a permissão `MANAGE ROLES` necessária para executar este comando!", "O usuário não tem permissão para executar o comando.");
 }

function errorMessage(error, errorCause = "none") {

 if (debug) {
 console.log(errorCause);
 }

 if (mainMessage != null) {
 mainMessage.delete();
 }

 let errorTemplate = {
 color: 0xFF0000,
 author: {
 name: client.user.username,
 icon_url: client.user.avatarURL
 },
        title: "Configuração do Sorteio",
 description: error,
 timestamp: new Date(),
 }

 msg.channel.send({embed: errorTemplate});
}

function askQuestion(question) {
 return new Promise(function(resolve, reject) {
 embedTemplate["description"] = question;
 mainMessage.edit({embed: embedTemplate});
 msg.channel.awaitMessages(response => response.author.id == msg.author.id, {
 max: 1,
 time: 60000,
 errors: ['time'],
 })
 .then(function(collected) {
 msg.author.lastMessage.delete();
 resolve(collected.first());
 }).catch((error) => {
 errorMessage('Desculpe, você não forneceu uma resposta válida a tempo! n\Por favor, execute novamente o comando para acessar o prompt novamente!', error, message);
 });
 })

}

const fs = require('fs');
const baseParam = msg.content.split(" ");
const embedID = baseParam[1];

msg.delete();

if (msg.member.hasPermission(['MANAGE_CHANNELS'])) {
 if (!isNaN(embedID) && embedID != null) {
 fs.readFile('./data/giveawayData.json', async function read(err, data) {
 if (err) {
 throw err;
 }
 data = JSON.parse(data);

 if (data.hasOwnProperty(msg.guild.id)) {
 var guildGiveaways = data[msg.guild.id];
 var giveaway = guildGiveaways.filter(object => object["id"] == embedID);

 if (giveaway.length != 0) {
 let giveawaysDue = giveaway[0];
 guildGiveaways.splice(guildGiveaways.findIndex(e => e.id == giveawaysDue.id), 1)

 fs.writeFile("./data/giveawayData.json", JSON.stringify(data), function(err) {
 if (err) {
 return console.log(err);
 }
 });

 var channelToFetch = await client.channels.cache.get(giveawaysDue["channel"]);
 if (channelToFetch != null) {
 var giveawayMessage = await channelToFetch.messages.fetch(giveawaysDue["giveawayMessageID"]);

 var giveawayEmojiCache = giveawayMessage.reactions.cache.get('?');
 var giveawayUsers = await giveawayEmojiCache.users.fetch();
                        if (giveawayEmojiCache.count > 100) {
 for (i = 0; i < giveawayEmojiCache.count; i++) {
 var fetchedEmojiUsers = await giveawayEmojiCache.users.fetch({
 after: giveawayUsers.last().id
 });
 await giveawayUsers.concat(fetchedEmojiUsers);
 }
 }

 giveawayUsers = giveawayUsers.array();
 giveawayUsers = await giveawayUsers.filter(object => object.id != client.user.id);
 var numberOfWinners = parseInt(giveawaysDue["winner"]);
 var gw = giveawayMessage.embeds[0];

 gw.color = 0x000000;
 if (gw.fields.length == 6) {
 gw.fields[5].value = "Finalizado"
 }
 gw.description = "**Sorteio Finalizado**\n\n" + gw.description

 var winnerArray = [];
 if (giveawayUsers.length > 0) {
 if (giveawayUsers.length > numberOfWinners) {
 for (var i = 0; i < numberOfWinners; i++) {
 var randomNumber = Math.floor(Math.random() * giveawayUsers.length);
 winnerArray.push(`${giveawayUsers[randomNumber]}`);
 giveawayUsers.splice(giveawayUsers.indexOf(randomNumber), randomNumber);
 }
 } else if (giveawayUsers.length <= numberOfWinners) {
 for (i = 0; i < giveawayUsers.length; i++) {
 winnerArray.push(`${giveawayUsers[i]}`);
 }
 }
 var winnerText = "";
 for (i = 0; i < winnerArray.length; i++) {
 winnerText = winnerText + winnerArray[i] + '\n';
 }

                            gw.description += `\n\nVencedor(es)..\n${winnerText}`;
 giveawayMessage.edit({
 embed: gw
 });
 channelToFetch.send(`? Parabéns! ? \n ${winnerText} \nPrêmio **${giveawaysDue["itemGivingAway"]}**`);

 } else {
 gw.description += "\n\nNinguém entrou no sorteio e, portanto, ninguém ganhou.";
 giveawayMessage.edit({
 embed: gw
 });
 channelToFetch.send(`? Sorteio Finalizado ?\n\nNinguém entrou no sorteio e, portanto, ninguém ganhou.`);
 }
 }
 } else {
 errorMessage("Mensagem do sorteio não encontrada.\nCertifique-se de que é um sorteio ativo e verifique novamente o id!");
 }
 } else {
 errorMessage("Este servidor não possui sorteios em andamento!")
 }
 })
 } else {
 errorMessage("Por favor, insira uma id de sorteio válida!")
 }
} else {
 errorMessage("Você não tem permissão para executar esse comando!");
}

function errorMessage(error) {
 let errorTemplate = {
 color: 0xFF0000,
 author: {
 name: client.user.username,
 icon_url: client.user.avatarURL
 },
        title: "Sorteio Finalizado",
 description: error,
 timestamp: new Date(),
 }

 msg.channel.send({
 embed: errorTemplate
 });
}

